// React Import
import React, { useContext, useEffect, useState } from "react";
// Styles Import
import styles from "./HeaderCartButton.module.css";
// Components Import
import CartIcon from "../Cart/CartIcon";
import CartContext from "../../Store/cart-context";

const HeaderCartButton = (props) => {
  const [btnIsHighlighted, setBtnIsHighlighted] = useState(false)

  const cartCtx = useContext(CartContext);
  const { items } = cartCtx;

  const numberOfCartItems = items.reduce((currentNumber, item) => {
    return currentNumber + item.amount
  },
  0);


  const btnClasses = `${styles.button} ${btnIsHighlighted ? styles.bump : ''}` // 2- if btnIdHighlighted is true, add the bump class otherwise not

  useEffect(() => {
    if (items.length === 0) {
      return;
    } else 
    setBtnIsHighlighted(true); // 1 - i change btnIsHighlighted to true

    setTimeout(() => {
      setBtnIsHighlighted(false);
    }, 300);
  }, [items])

  return (
    <button className={btnClasses} onClick={props.onCartClick}>
      <span className={styles.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={styles.badge}>{numberOfCartItems}</span>
    </button>
  );
};

export default HeaderCartButton;
